import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import { cedict } from "./test.js";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'; // This was necessary; ugh
import { Container, Row, Col } from 'react-bootstrap';
// import { cedict } from "./cedict.js"  // think it works; crashed everything
// import { } from 'react-bootstrap';  // 

// function Translator() {  // how do I call this, below? like -- anything else I imagine
//   return (
//     <div>
//       <input type="text"></input>
//       <button>Translate</button>
//     </div>
//   )
// }

function App() {

  function Test(props) {
    const hello = cedict['hello']
    return (
      <h1>{hello}</h1>
    )
  }

  function Translation(props) { 
    // This is super sloppy for all sorts of reasons; do need the chinese punctuation though
    const punctuation = ["，", "。", "「", "」", "、", "‧", ".", ";", " ", "\t"]

    const translation = []
    let remaining_characters = props.text
    while (remaining_characters.length > 0) {
        let word_boundary = 1
        const best_candidate = null
        while (remaining_characters.length > 0) {
            const candidate = remaining_characters.slice(0, word_boundary)  // [:word_boundary] // XXX
            if ((candidate in cedict) && (word_boundary <= remaining_characters.length)) {
                best_candidate = candidate
                word_boundary += 1
            } else {  // candidate is NOT in the dictionary
                if (best_candidate != null) {  // at least the first character was recognized
                    translation.push([best_candidate, cedict[best_candidate]])
                } else {
                    const token = remaining_characters[0]
                    const definition = token in punctuation ? "" : "??"  // "\n" if token in punctuation else "??\n"  // 
                    translation.push({"token": token, "definition": definition} )
                }
                if (word_boundary > 1) {
                    remaining_characters = remaining_characters.slice(word_boundary-1)  // [word_boundary-1:]
                } else {
                    remaining_characters = remaining_characters.slice(word_boundary)  // [word_boundary:]
                }
                break
            }
        }
    }
    return(
      translation.map((word, ix) =>
        <Row>
          <Col md={4}>{word["token"]}</Col>
          <Col md={8}>{word["definition"]}</Col>
        </Row>
      )
    )
  }

  return (
    <div className="App">

      <h1>Hello react!</h1>
      <input type="text"></input>
      {/* <button onClick={() => Translation()}>
        Translate
      </button> */}
      <br></br>
      <Translation 
        text="十月十一日"
      />
    </div>
  );
}

export default App;
