import React, { useState, useEffect } from 'react';
import './App.css';
import { cedict } from "./small.js";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css'; // This was necessary; ugh
import { Container, Row, Col } from 'react-bootstrap';


class Translator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      translation: []
    };
    // Would prefer the less insane syntax for this
    this.handleInputChange = this.handleInputChange.bind(this)
  }

  // The translation function
  translate = function Translation(props) { 
    // This is super sloppy for all sorts of reasons; do need the chinese punctuation though
    const punctuation = ["，", "。", "「", "」", "、", "‧", ".", ";", " ", "\t"]

    const translation = []
    let remaining_characters = props.text
    while (remaining_characters.length > 0) {
        let word_boundary = 1
        let best_candidate = null
        while (remaining_characters.length > 0) {
            const candidate = remaining_characters.slice(0, word_boundary)  // [:word_boundary] // XXX
            // console.log(candidate)
            // console.log(candidate in cedict) // fuck; different ... something. Fuck!
            if ((candidate in cedict) && (word_boundary <= remaining_characters.length)) {
                best_candidate = candidate
                word_boundary += 1
            } else {  // candidate is NOT in the dictionary
                if (best_candidate != null) {  // at least the first character was recognized
                  const line = String(cedict[best_candidate])
                  const pinyin = (line.split(/](.+)/)[0]) + "]"
                  const definition = (line.split(/](.+)/)[1]).slice(1) // I only want to split on the FIRST; 
                  translation.push({"token": best_candidate, "pinyin": pinyin, "definition": definition})
                } else {
                    const token = remaining_characters[0]
                    const definition = token in punctuation ? "" : "??"  // "\n" if token in punctuation else "??\n"  // 
                    translation.push({"token": token, "pinyin": "", "definition": ""}) // definition} )
                }
                if (word_boundary > 1) {
                    remaining_characters = remaining_characters.slice(word_boundary-1)  // [word_boundary-1:]
                } else {
                    remaining_characters = remaining_characters.slice(word_boundary)  // [word_boundary:]
                }
                break
            }
        }
    }
    return(translation)
  }


  render() {
    return(
      <Container>
        <input
            type="text"
            // value={ this.state.text }
            onChange={ this.handleInputChange }
        />
        {this.state.translation.map((word, ix) =>
          <Row key={ix} style={{textAlign: "left"}}>
            <Col md={2}>{word["token"]}</Col> 
            <Col md={2}>{word["pinyin"]}</Col>
            <Col md={8}>{word["definition"]}</Col>
          </Row>
        )}
      </Container>
    )  }
}

function App() {

  function Translation(props) { 
    // This is super sloppy for all sorts of reasons; do need the chinese punctuation though
    const punctuation = ["，", "。", "「", "」", "、", "‧", ".", ";", " ", "\t"]

    const translation = []
    let remaining_characters = props.text
    while (remaining_characters.length > 0) {
        let word_boundary = 1
        let best_candidate = null
        while (remaining_characters.length > 0) {
            const candidate = remaining_characters.slice(0, word_boundary)  // [:word_boundary] // XXX
            console.log(candidate)
            console.log(candidate in cedict) // fuck; different ... something. Fuck!
            if ((candidate in cedict) && (word_boundary <= remaining_characters.length)) {
                best_candidate = candidate
                word_boundary += 1
            } else {  // candidate is NOT in the dictionary
                if (best_candidate != null) {  // at least the first character was recognized
                  const line = String(cedict[best_candidate])
                  const pinyin = (line.split(/](.+)/)[0]) + "]"
                  const definition = (line.split(/](.+)/)[1]).slice(1) // I only want to split on the FIRST; 
                  translation.push({"token": best_candidate, "pinyin": pinyin, "definition": definition})
                } else {
                    const token = remaining_characters[0]
                    const definition = token in punctuation ? "" : "??"  // "\n" if token in punctuation else "??\n"  // 
                    translation.push({"token": token, "pinyin": "", "definition": ""}) // definition} )
                }
                if (word_boundary > 1) {
                    remaining_characters = remaining_characters.slice(word_boundary-1)  // [word_boundary-1:]
                } else {
                    remaining_characters = remaining_characters.slice(word_boundary)  // [word_boundary:]
                }
                break
            }
        }
    }
    return(
      <Container>
        <input type="text"></input>
        {translation.map((word, ix) =>
          <Row key={ix} style={{textAlign: "left"}}>
            <Col md={2}>{word["token"]}</Col> 
            <Col md={2}>{word["pinyin"]}</Col>
            <Col md={8}>{word["definition"]}</Col>
          </Row>
        )}
      </Container>
    )
  }

  return (
    <div className="App">

      <h1>Ladder of Babel</h1>
      <Translation />
    </div>
  );
}

export default App;

